package com.cards.authorization.transactions.service.application.configimport org.springframework.beans.factory.annotation.Valueimport org.springframework.boot.context.properties.ConfigurationPropertiesimport org.springframework.boot.context.properties.EnableConfigurationPropertiesimport org.springframework.context.annotation.Configuration@Configuration()@EnableConfigurationProperties(    VisaMccPropertiesConfig::class)class CodeOperationsConfiguration@ConfigurationProperties(prefix = "service.config.engine-flag.visa")@EnableConfigurationPropertiesdata class VisaMccPropertiesConfig(    @Value("\${service.config.engine-flag.visa.mcc-operations-allowed}")    val mccOperationsAllowed: Map<String, String>) {    fun getTypeOperations(code: String): Pair<String, String>? {        for ((key, value) in mccOperationsAllowed) {            val valueList = value.split(",").map { it.trim() }            if (valueList.contains(code)) {                Pair(key, code)            }        }        return null    }}