package com.cards.authorization.transactions.service.application.configimport com.cards.authorization.transactions.service.application.service.strategy.CashCategoryAuthorizationStrategyimport com.cards.authorization.transactions.service.application.service.strategy.PrimaryCategoryAuthorizationStrategyimport com.cards.authorization.transactions.service.domain.account.AccountRepositoryimport com.cards.authorization.transactions.service.domain.category.CategoryRepositoryimport org.springframework.context.annotation.Beanimport org.springframework.context.annotation.Configuration@Configurationclass StrategyBean {    @Bean    fun primaryCategoryAuthorizationStrategy(        accountRepository: AccountRepository    ): PrimaryCategoryAuthorizationStrategy {        return PrimaryCategoryAuthorizationStrategy(accountRepository)    }    @Bean    fun cashCategoryAuthorizationStrategy(        accountRepository: AccountRepository,        categoryRepository: CategoryRepository    ): CashCategoryAuthorizationStrategy {        return CashCategoryAuthorizationStrategy(accountRepository)    }}