package com.cards.authorization.transactions.service.application.config.httpimport feign.Feignimport feign.codec.ErrorDecoderimport feign.okhttp.OkHttpClientimport org.springframework.context.annotation.Beanimport org.springframework.context.annotation.Configuration@Configurationclass FeignConfig {    @Bean    fun feignClient(): Feign =        Feign.builder()            .errorDecoder(feignCustomError())            .client(OkHttpClient()).build()    @Bean    fun feignCustomError(): ErrorDecoder = FeignCustomError()}