package com.cards.authorization.transactions.service.application.exceptionsimport com.cards.authorization.transactions.service.application.exceptions.ExceptionTypes.BAD_REQUESTclass BadRequestException(    override val message: String,    override val cause: Throwable? = null,    override val details: Map<String, Any> = mapOf()) : BaseException(    type = BAD_REQUEST,    message = message,    cause = cause,    details = details)