package com.cards.authorization.transactions.service.application.web.controller.authorization.dto.requestimport com.cards.authorization.transactions.service.application.service.dto.TransactionDTOimport java.io.Serializableimport java.math.BigDecimaldata class TransactionRequest(    val transactionId: String,    val accountId: String,    val totalAmount: BigDecimal,    val mcc: String,    val merchant: String) : Serializablefun TransactionRequest.toTransactionDTO() = TransactionDTO(    transactionId = transactionId,    accountId = accountId,    totalAmount = totalAmount,    mcc = mcc,    merchant = merchant)