package com.cards.authorization.transactions.service.repositories.database.categoryimport com.cards.authorization.transactions.service.repositories.database.category.CategoryQuery.FIND_BY_CATEGORY_IDimport com.cards.authorization.transactions.service.repositories.database.category.CategoryQuery.FIND_BY_CATEGORY_TYPEimport com.cards.authorization.transactions.service.repositories.database.category.entity.CategoryEntityimport org.springframework.data.jpa.repository.JpaRepositoryimport org.springframework.data.jpa.repository.Queryimport org.springframework.data.repository.query.Paramimport org.springframework.stereotype.Repository@Repositoryinterface CategoryRepositoryJpa : JpaRepository<CategoryEntity, String> {    @Query(FIND_BY_CATEGORY_ID, nativeQuery = true)    fun findByCategoryId(@Param("id") id: String): CategoryEntity?    @Query(FIND_BY_CATEGORY_TYPE, nativeQuery = true)    fun findByCategoryType(@Param("type") type: String): CategoryEntity?}