create table if not exists accounts(       external_id VARCHAR(26) not null,        account_id VARCHAR(26) not null,        category_id VARCHAR(26) not null,        person_id VARCHAR(26) not null,       balance DECIMAL(10,2) not null default 0.00,              primary key(category_id, person_id),              constraint fk_account_category_id_idx foreign key(category_id) references category(id),       constraint fk_person_id_idx foreign key(person_id) references person(id))--MEALinsert into accounts values ( '01JJZT3VFASZZQQVK2Y38Q6Q64', '01JJT4DW33GK17K4GN3NRHY4TV', '01JJT1Z50Q33BYJ4NDF3RQ09VR', 300.00)--FOODinsert into accounts values ( '01JJZT7FHTK3JAZNDFZWTQM7P2', '01JJT229BC88F3CX1YXJ9S93D6', '01JJT1Z50Q33BYJ4NDF3RQ09VR', 300.00)--CASHinsert into accounts values ( '01JJWYEC9BY1XVN2KSM90SHMSW', '01JJTZN4Y8HT7HWZ1QE9ZEHAA1', '01JJT1Z50Q33BYJ4NDF3RQ09VR', 300.00)