package com.cards.authorization.transactions.service.common.extensionimport com.cards.authorization.transactions.service.common.extension.bodyStringimport com.cards.authorization.transactions.service.common.extension.getDetailsimport feign.Responseimport feign.Response.Bodyimport io.mockk.everyimport io.mockk.impl.annotations.MockKimport io.mockk.junit5.MockKExtensionimport org.junit.jupiter.api.Testimport org.junit.jupiter.api.assertDoesNotThrowimport org.junit.jupiter.api.extension.ExtendWithimport java.io.StringReader@ExtendWith(MockKExtension::class)class FeignExtKtTest {    @MockK    lateinit var response: Response    @MockK    lateinit var body: Body    @Test    fun `check getDetails`() {        print(assertDoesNotThrow { getDetails(BAD_REQUEST_RESPONSE) })    }    @Test    fun `check bodyString`() {        every { response.body() } returns body        every { body.asReader(any()) } returns StringReader("body")        assertDoesNotThrow { response.bodyString("Test function bodyString()") }    }}