package com.cards.authorization.transactions.service.commonsimport com.cards.authorization.transactions.service.domain.account.entites.types.AccountCategoryTypeimport com.cards.authorization.transactions.service.repositories.database.category.entity.CategoryEntityimport java.time.LocalDateTimeobject CategoryBuilder {    fun buildCategory(        id: String = Constants.CATEGORY_ID,        type: AccountCategoryType = AccountCategoryType.FOOD,        createdAt: LocalDateTime = LocalDateTime.now(),        updatedAt: LocalDateTime = LocalDateTime.now()    ) = CategoryEntity(        id = id,        type = type,        createdAt = createdAt,        updatedAt = updatedAt    )}