package com.cards.authorization.transactions.service.commons.extensionimport com.cards.authorization.transactions.service.application.config.http.Constants.AUTHORIZATION_HEADER_KEYimport com.cards.authorization.transactions.service.application.config.http.Constants.CONTENT_TYPE_HEADER_KEYimport feign.Requestimport feign.RequestTemplateimport feign.Responseval POST_REQUEST_BUILDER: Request = Request.create(    Request.HttpMethod.POST,    "url",    mapOf(AUTHORIZATION_HEADER_KEY to listOf("Bearer 12342"), CONTENT_TYPE_HEADER_KEY to listOf("application/json")),    null,    RequestTemplate())val BAD_REQUEST_RESPONSE: Response =    Response        .builder()        .status(400)        .headers(emptyMap())        .reason("Bad request")        .request(POST_REQUEST_BUILDER)        .build()